mapper.select:
SELECT ci.id, ci.campusid, ci.name as name, ci.classtime, ci.lecturer, ci.teacher, cm.name as campusname, ci.createtime FROM classinfo ci INNER JOIN campusmanage cm ON ci.campusid = cm.id where 1=1
AND ci.campusid = #{campusid}
AND ci.name LIKE CONCAT('%',#{name},'%')
AND ci.classtime = #{classtime}
